mapper.select:
select t.id as id,t.`name` as name, t.start_date,t.end_date,t.cost,t.description,t.create_time,u1.`name` as owner,u2.name as create_by,u3.name as edit_by from tbl_activity t left join tbl_user u1 on t.`owner` = u1.id left join tbl_user u2 on t.create_by = u2.id left join tbl_user u3 on t.edit_by = u3.id
and t.name like CONCAT('%',#{name},'%')
and u1.name like CONCAT('%',#{owner},'%')
and t.start_date >= #{startDate}
and t.end_date <= #{endDate}
order by t.create_time desc limit #{pageNo},#{pageSize}

select count(t.id) from tbl_activity t left join tbl_user u1 on t.`owner` = u1.id left join tbl_user u2 on t.create_by = u2.id left join tbl_user u3 on t.edit_by = u3.id
and t.name like CONCAT('%',#{name},'%')
and u1.name like CONCAT('%',#{owner},'%')
and t.start_date >= #{startDate}
and t.end_date <= #{endDate}
